//alt1 base libs, provides all the commonly used methods for image matching and capture
//also gives your editor info about the window.alt1 api
import * as A1lib from "@alt1/base";
import * as BuffReader from "@alt1/buffs";

let font = require("@alt1/ocr/fonts/pixel_digits_8px_shadow.js");

//tell webpack to add index.html and appconfig.json to output
require("!file-loader?name=[name].[ext]!./index.html");
require("!file-loader?name=[name].[ext]!./appconfig.json");

let imgs = A1lib.ImageDetect.webpackImages(
    {
        "incite_red": require("./images/incite_red.data.png"),
        "incite_red_cropped": require("./images/incite_red_cropped.data.png"),
	}
);


let reader = new BuffReader.default();
function showSelectedBuffs(chat) {
    let buffsize = 27;
	let gridsize = 30;
    console.log(chat);
    // Attempt to show a temporary rectangle around the chatbox.  skip if overlay is not enabled.
    try {
      alt1.overLayRect(
        A1lib.mixColor(0, 255, 0),
        chat.x,
        chat.y,
        chat.width,
        chat.height,
        2000,
        5
      );
    } catch { }
}

function readBuffs() {
    let opts = reader.read() || [];
    let canvas = document.getElementById("canvas") as HTMLCanvasElement;
    let ctx = canvas.getContext("2d");

    // Clear the canvas and draw the empty buff icon
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    for (const a in opts) {
        if (opts[a].compareBuffer(imgs.incite_red_cropped)) {            
            let img = opts[a].buffer.toImage();
            if (localStorage["incite_alert"] == opts[a].readTime())
            {
                console.log("ALERT");
                var snd = new Audio("data:audio/wav;base64,");  
                snd.volume = 0.05;
                snd.play();
            }
                

            // drawImage(image, sx, sy, sWidth, sHeight, dx, dy, dWidth, dHeight);        
            ctx.drawImage(img, opts[a].bufferx, opts[a].buffery, 27, 27, 0, 0, canvas.width, canvas.height);
            return;
        }
    }

    let img = imgs.incite_red.toImage();
    ctx.drawImage(img, 0, 0, 27, 27, 0, 0, canvas.width, canvas.height);
}

document.getElementById("alertAt").onchange = function (e) {
    let alert_at = parseInt((<HTMLInputElement>this).value);
    localStorage["incite_alert"] = alert_at;
}

document.oncontextmenu = function (e) {
    // Toggle canvas and alert input
    document.getElementById("alert_input").hidden = !document.getElementById("alert_input").hidden;
    document.getElementById("canvas").hidden = !document.getElementById("canvas").hidden;

    e.preventDefault();
}

// check if we are running inside alt1 by checking if the alt1 global exists
if (window.alt1) {
    alt1.identifyAppUrl("./appconfig.json");
    
    if (localStorage["incite_alert"] == undefined)
        localStorage["incite_alert"] = 3;

    document.getElementById("alertAt").setAttribute("value", localStorage["incite_alert"]);
    document.getElementById("canvas").hidden = true;

    console.log("Hello world");
    let findBuffBar = setInterval(function () {
        if (reader.pos === null)
        {
            reader.debuffs = true;
            reader.find();
            console.log("bad");
        }            
        else 
        {
            clearInterval(findBuffBar);
            let square = reader.getCaptRect();
            showSelectedBuffs(square);

            document.getElementById("alert_input").hidden = true;
            document.getElementById("canvas").hidden = false;
            console.log("good");
            setInterval(function () {
                readBuffs();
            }, 100);
        }
    }, 1000);
}
else
{
    console.log("Fuck you and your stupid app");
}